from bs4 import BeautifulSoup


class ComponentRenderer:
    def __init__(self, html):
        self.soup = BeautifulSoup(html, "html.parser")
        self.components = self.soup.find_all("div", class_="component")

    def detect_type(self, comp):
        if comp.find("img") and comp.find("a"):
            # 若 table 中有 class wrapper_ad 且圖片橫向排列
            if comp.find("table", class_="responsive-table"):
                return "product"
            return "banner"
        elif comp.find("a") and not comp.find("img"):
            return "button"
        else:
            return "text"

    def render_text(self, comp):
        content = comp.get_text(separator="<br/>", strip=True)
        return f'<div style="text-align:center; margin:10px 0;">{content}</div>'

    def render_banner(self, comp):
        link = comp.find("a")
        img = comp.find("img")
        if link and img:
            href = link.get("href", "#")
            src = img.get("src", "")
            return f'<div style="text-align:center; margin:10px 0;"><a href="{href}" target="_blank"><img src="{src}" style="max-width:100%; height:auto;"></a></div>'
        return ""

    def render_product(self, comp) -> str:
        tables = comp.find_all("table", class_="responsive-table js-component-block")
        seen = set()
        cols_html = []

        for tbl in tables:
            link_tag = tbl.find("a")
            if not link_tag:
                continue
            href = link_tag.get("href")
            if not href or href in seen:
                continue
            seen.add(href)

            img_tag = tbl.find("img")
            img_src = img_tag.get("src") if img_tag else ""

            # 商品標題
            title_tag = tbl.find_all("td", style=lambda x: x and "font-weight: bold" in x)
            title_text = title_tag[0].get_text(strip=True) if title_tag else ""
            # 價格
            price_text = title_tag[1].get_text(strip=True) if len(title_tag) > 1 else ""

            col_html = f"""
            <td valign="top" style="padding:10px;">
                <a href="{href}" target="_blank">
                    <img src="{img_src}" style="max-width:145px; height:auto;"><br>
                    <div style="font-size:14px; color:#337ab7;">{title_text}</div>
                    <div style="font-size:14px; color:#bf0000;">{price_text}</div>
                </a>
            </td>
            """
            cols_html.append(col_html)

        if not cols_html:
            return ""

        return "<table align='center'><tr>" + "".join(cols_html) + "</tr></table>"

    def render_button(self, comp):
        link = comp.find("a")
        if link:
            text = link.get_text(strip=True)
            href = link.get("href", "#")
            return f'<div style="text-align:center; margin:10px 0;"><a href="{href}" target="_blank" style="display:inline-block; padding:10px 30px; background-color:#bf0001; color:#fff; text-decoration:none; border-radius:5px;">{text}</a></div>'
        return ""

    def render_all(self):
        rendered = []
        for comp in self.components:
            ctype = self.detect_type(comp)
            print(ctype)
            if ctype == "text":
                rendered.append(self.render_text(comp))
            elif ctype == "banner":
                rendered.append(self.render_banner(comp))
            elif ctype == "product":
                rendered.append(self.render_product(comp))
            elif ctype == "button":
                rendered.append(self.render_button(comp))
        html = f'<html><head><meta charset="utf-8"><title>Components</title></head><body style="text-align:center; max-width:900px; margin:0 auto;">{"".join(rendered)}</body></html>'
        return html


